
package learnstring;

public class StringReverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      String name = "Geetha";
      int length = name.length();
      for(int i=length-1;i>=0;i--)
      {
    	  System.out.print(name.charAt(i));
      }
	}

}
